//pelo nex pode usar o import
import {GoogleSpreadsheet} from 'google-spreadsheet'

import credentials from '../../credentials.json'

const doc = new GoogleSpreadsheet('1RS3tQeiyuF9S25VHOLdkuCCMR8vQncxEuyXYg20wE-M ')

export default async(req,res) =>{

    try{
        await doc.useServiceAccountAuth(credentials)
        await doc.loadInfo()

        const sheet = doc.sheetsByIndex[1]
        await sheet.loadCells('A3:B3')

        const celpromo = sheet.getCell(2 ,0)
        const celltexto = sheet.getCell(2,1)
        

        res.end(JSON.stringify({
            showCupon : celpromo.value === 'VERDADEIRO',
            message : celltexto.value
        }))
    }catch(err){
        res.end(JSON.stringify({
            showCupon : false,
            message :    ''    
       }))
    }
    
}
